from flask import Flask, request ,render_template
from ayırt_edici import class_name

app = Flask(__name__)

@app.route('/', methods=['GET', 'POST'])
def image_upload():
    if request.method == 'POST':
        if 'file' not in request.files:
            return "Resim yüklenemedi"
            
        file = request.files['file']
        if file.filename == '':
            return "Resim seçilmedi"
            
            # Resmi belirli bir klasöre kaydet
        file.save('uploads/' + file.filename)
        return "Resim başarıyla yüklendi"

    return '''
        <form method="post" enctype="multipart/form-data">
            <label for="file">Resim Seç:</label>
            <input type="file" name="file" id="file">
            <input type="submit" value="Resmi Yükle">
        </form>
    '''
